abel for=\"text-input\" class=\"col-sm-3 control-label\">Vendor(s)</label>\n\n							<div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6\" >\n\n								<select id=\"vendors\" name=\"vendors[]\" class=\"form-control input multiselect\" multiple=\"multiple\" >\n						\n\n								</select>\n							</div>\n																			<div class=\"col-xs-4 col-sm-2 col-md-2 col-lg-2\" >\n\n\n								<input type=\"checkbox\" id=\"allVendors\" name=\"allVendors\" {if $report.allVendors}checked{/if} class=\"switch switch-sm\" data-on=\"success\" data-off=\"default\" data-on-label=\"All\" data-off-label=\"All\" >\n	\n							</div>\n\n					</div>\n\n					<div class=\"form-group\">\n																			<label for=\"text-input\" class=\"col-sm-3 control-label\">Include Net Cost?</label>\n																			<div class=\"col-xs-4 col-sm-2 col-md-2 col-lg-2\" >\n\n								<input type=\"checkbox\" id=\"includeNet\" name=\"includeNet\" {if $report.includeNet}checked{/if} class=\"switch switch-sm\" data-on=\"success\" data-off=\"default\" data-on-label=\"Yes\" data-off-label=\"No\" >\n	\n							</div>\n\n					</div>\n\n					<div class=\"form-group\">\n																			<label for=\"text-input\" class=\"col-sm-3 control-label\">Format?</label>\n\n							<div class=\"col-xs-6 col-sm-6 col-md-6 col-lg-6\" style=\"white-space: nowrap;\" nowrap>\n\n								<select id=\"format\" name=\"format\"  class=\"form-control\" >\n									{foreach $formats as $format}										<option value=\"{$format.id}\">{$format.name}</option>\n									{foreachelse}\n										<option value=\"pdf\" selected>PDF</option>\n										<option value=\"csv\" disabled>XLS/SV</option>\n										<option value=\"web\" disabled>Web</option>\n									{/foreach}\n								</select>\n	\n							</div>\n\n					</div>\n\n					<div class=\"form-group\">\n						<div class=\"col-sm-12\">\n							<p class=\"pull-right\">\n								<button type=\"button\" id=\"createReportButton\" class=\"btn btn-success\" onclick=\"createReport();\">Create</button>\n							</p>\n						</div>\n					</div>		\n															</form>\n\n		</div>\n\n		<div id=\"report-footer\" class=\"widget-footer\">\n		</div>\n\n	</div>\n\n<div class=\"modal fade\" id=\"reportWait\">\n  <div class=\"modal-dialog\">\n    <div class=\"modal-content\">\n      <div class=\"modal-header\">\n        <h4 class=\"modal-title\">Please Wait</h4>\n      </div>\n      <div class=\"modal-body\">\n		<div class=\"progress progress-striped active\">\n		  <div class=\"progress-bar progress-bar-success\" role=\"progressbar\" aria-valuenow=\"40\" aria-valuemin=\"0\" aria-valuemax=\"100\" style=\"width: 100%\">\n		  </div>\n		</div>\n		<span class=\"reportStatus\">Creating Report...</span>\n      </div>\n\n    </div><!-- /.modal-content -->\n  </div><!-- /.modal-dialog -->\n</div><!-- /.modal -->\n\n\n\n\n\n</div>\n</div>\n\n{/block}\n\n{block name=\"footer-js\"}\n	<script type=\"text/javascript\" src=\"assets/js/bootstrap-switch.min.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/jquery.masked-input.min.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/bootstrap-multiselect.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/parsley.min.js\"></script>\n	<script type=\"text/javascript\" src=\"/lib/bootbox/bootbox-4.2.0/bootbox.min.js\"></script>\n\n	<script type=\"text/javascript\" src=\"assets/js/datatable/jquery.dataTables.min.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/datatable/jquery.dataTables.bootstrap.js\"></script>\n\n	<script type=\"text/javascript\" src=\"assets/js/bootstrap-datepicker.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/daterangepicker.js\"></script>\n	<script type=\"text/javascript\" src=\"assets/js/moment.min.js\"></script>\n\n    	<script type=\"text/javascript\" src=\"/lib/select2/select2.js\"></script>\n\n	<script>\n\n		$(document).ready(function(){\n\n			$(\'#allClients\').bootstrapSwitch();\n			$(\'#allTypes\').bootstrapSwitch();\n			$(\'#allCampaigns\').bootstrapSwitch();\n			$(\'#includeNet\').bootstrapSwitch();\n			$(\'#allVendors\').bootstrapSwitch();\n	\n			$(\'#reportDates\').daterangepicker({\n				startDate: moment().add(\'days\', 1),\n				endDate: moment().add(\'days\', 30),\n				minDate: \'01/01/1900\',\n				maxDate: \'12/31/2019\',\n				dateLimit: false,\n				showDropdowns: true,\n				showWeekNumbers: true,\n				timePicker: false,\n				timePickerIncrement: 1,\n				timePicker12Hour: true,\n				ranges: {\n					\'Today\': [moment(), moment()],\n					\'Tomorrow\': [moment().add(\'days\', 1), moment().add(\'days\', 1)],\n					\'Next 7 Days\': [moment().add(\'days\', 1),moment().add(\'days\', 7)],\n					\'Next 30 Days\': [moment().add(\'days\', 1), moment().add(\'days\', 30)],\n					\'Last Month\': [moment().subtract(\'month\', 1).startOf(\'month\'), moment().subtract(\'month\', 1).endOf(\'month\')],\n					\'This Month\': [moment().startOf(\'month\'), moment().endOf(\'month\')],\n					\'Next Month\': [moment().add(\'month\', 1).startOf(\'month\'), moment().add(\'month\', 1).endOf(\'month\')],\n					\'This Year To Date\': [moment().startOf(\'year\'), moment()],\n					\'Last Year\': [moment().subtract(\'year\', 1).startOf(\'year\'), moment().subtract(\'year\', 1).endOf(\'year\')]\n					},\n				opens: \'left\',\n				buttonClasses: [\'btn btn-default\'],\n				applyClass: \'btn-small btn-primary\',\n				cancelClass: \'btn-small\',\n				format: \'MM/DD/YYYY\',\n				separator: \' to \',\n				locale: {\n						applyLabel: \'Submit\',\n						fromLabel: \'From\',\n						toLabel: \'To\',\n						customRangeLabel: \'Custom Range\',\n						daysOfWeek: [\'Su\', \'Mo\', \'Tu\', \'We\', \'Th\', \'Fr\',\'Sa\'],\n						monthNames: [\'January\', \'February\', \'March\', \'April\', \'May\', \'June\', \'July\', \'August\', \'September\', \'October\', \'November\', \'December\'],\n						firstDay: 1\n					}\n				},\n	\n				function(start, end) {\n	\n					var startDate = new Date(start);\n					$(\'#reportDates span\').html(start.format(\'MMMM D, YYYY\') + \' - \' + end.format(\'MMMM D, YYYY\'));\n				\n				}\n			);\n\n			$(\'#reportDates span\').html(moment().add(\'days\', 1).format(\'MMMM D, YYYY\') + \' - \' + moment().add(\'days\', 30).format(\'MMMM D, YYYY\'));\n\n			$(\"#allClients\").change(function () {\n				allClients();\n			}); \n\n			$(\"#allTypes\").change(function () {\n				allTypes();\n			}); \n\n			$(\"#allCampaigns\").change(function () {\n				allCampaigns();\n			}); \n\n			$(\"#includeNet\").change(function () {\n				includeNet();\n			}); \n\n			$(\"#allVendors\").change(function () {\n				allVendors();\n			}); \n\n			$(\"#clients\").change(function () {\n				getCampaigns($(\"#clients\").val());\n			        $(\'#vendors\').select2(\"val\", \"\");\n			        $(\'#vendors\').empty();\n			        $(\'#markets\').select2(\"val\", \"\");\n			        $(\'#markets\').empty();\n			}); \n\n			$(\"#campaigns\").change(function () {\n				getVendors($(\"#campaigns\").val());\n			        $(\'#markets\').select2(\"val\", \"\");\n			        $(\'#markets\').empty();\n			}); \n\n			$(\"#vendors\").change(function () {\n				getMarkets($(\"#vendors\").val());\n			}); \n\n			$(\"#clients\").select2({\n			    placeholder: \"Search Clients\",\n			    allowClear: true\n			});\n\n			$(\"#vendors\").select2({\n			    placeholder: \"Search Vendors\",\n			    allowClear: true\n			});\n\n			$(\"#campaigns\").select2({\n			    placeholder: \"Search Campaigns\",\n			    allowClear: true\n			});\n\n			$(\"#format\").select2({\n			    placeholder: \"Report Format\",\n			    allowClear: false\n			});\n\n			$(\"#marketType\").select2({\n			    placeholder: \"Report Type\",\n			    allowClear: false\n			});\n\n			$(\"#campaigns\").attr(\"disabled\",\"disabled\");\n			$(\"#types\").attr(\"disabled\",\"disabled\");\n\n			if ($(\"#clients\").val() != \"\") {\n				getCampaigns($(\"#clients\").val());\n			}\n		});\n\n	</script>\n\n	<script type=\"text/javascript\">\n\n		function resetFooter() {\n				$(\"#client-footer\").html(\"Not Saved\");\n		}\n\n		function clientActive() {\n\n			if ($(\"#clientActive\").is(\":checked\")) {\n				$(\"#client-active\").val(\"1\");\n			}\n			else {\n				$(\"#client-active\").val(\"0\");\n			}\n		}\n\n	 	 function createClient() {\n	  \n	  		alert(\"saving client\");\n			  		 		  \n	  	}\n\n		function saveClient() {\n			\n			$(\"#client-footer\").html(\"Saving.\");\n\n			clientActive();\n\n			$.post( \"/clients/ajax/save/{$client.id}\"+ \"?PHPSESSID={$sessionID}\", $( \"#client-form\" ).serialize(), function() {\n				$(\"#client-footer\").html(\"Saving...\");\n			})\n 			.done(function(data) {\n			        var response = jQuery.parseJSON(data);\n				$(\"#client-footer\").html(response.message);\n				if (response.error) {\n					$(\"#\"+response.field).addClass(\"has-error error\");\n					$(\"#client-name-help\").show();\n\n				}\n				else {\n					$(\"#client-name\").removeClass(\"has-error error\");\n					$(\"#client-name-help\").hide();\n				}\n				$(\"#breadcrumb-client-name\").html($(\"#client-name-input\").val());\n\n			})\n			.fail(function(data) {\n				$(\"#client-footer\").html(\"Connection Failed! Not Saved.\");\n			})\n			.error(function(data) {\n				$(\"#client-footer\").html(\"Error!\");\n			})\n		}\n	\n		function getCampaigns(client) {\n\n			$.post( \"/campaigns/ajax/search/campaigns/clientID/\" + client + \"?PHPSESSID={$sessionID}\",  function() {\n				$(\"#report-footer\").html(\"Searching For Campaigns...\");\n			})\n 			.done(function(data) {\n			        var response = jQuery.parseJSON(data);\n				console.log(response);\n				$(\"#report-footer\").html(response.message);\n				if (response.error) {\n					$(\"#campaigns\").attr(\"disabled\",\"disabled\");\n				}\n				else {\n					$(\"#campaigns\").attr(\"disabled\", false);\n				        $(\'#campaigns\').select2(\"val\", \"\");\n				        var select = $(\'#campaigns\').empty();\n					  $.each(response.results, function(i,item) {\n           					 select.append( \'<option value=\"\'\n                           			      + item.id\n                            			      + \'\">\'\n                          			      + item.name\n                            			      + \'</option>\' ); \n   				          });\n				}\n\n			})\n			.fail(function(data) {\n				$(\"#report-footer\").html(\"Connection Failed!\");\n			})\n			.error(function(data) {\n				$(\"#report-footer\").html(\"Error!\");\n			})\n		}\n\n	\n		function getVendors(campaign) {\n\n			$.post( \"/campaigns/ajax/search/vendors/campaignID/\" + campaign + \"?PHPSESSID={$sessionID}\" ,  function() {\n				$(\"#report-footer\").html(\"Searching For Vendors...\");\n			})\n 			.done(function(data) {\n			        var response = jQuery.parseJSON(data);\n				console.log(response);\n				$(\"#report-footer\").html(response.message);\n				if (response.error) {\n					$(\"#vendors\").attr(\"disabled\",\"disabled\");\n				}\n				else {\n					$(\"#vendors\").attr(\"disabled\", false);\n				        $(\'#vendors\').select2(\"val\", \"\");\n				        var select = $(\'#vendors\').empty();\n					  $.each(response.results, function(i,item) {\n           					 select.append( \'<option value=\"\'\n                           			      + item.id\n                            			      + \'\">\'\n                          			      + item.name\n                            			      + \'</option>\' ); \n   				          });\n				}\n\n			})\n			.fail(function(data) {\n				$(\"#report-footer\").html(\"Connection Failed!\");\n			})\n			.error(function(data) {\n				$(\"#report-footer\").html(\"Error!\");\n			})\n		}\n\n	\n		function getMarkets(vendor) {\n\n			$.post( \"/campaigns/ajax/search/markets/marketVendorID/\" + vendor + \"?PHPSESSID={$sessionID}\",  function() {\n				$(\"#report-footer\").html(\"Searching For Markets...\");\n			})\n 			.done(function(data) {\n			        var response = jQuery.parseJSON(data);\n				console.log(response);\n				$(\"#report-footer\").html(response.message);\n				if (response.error) {\n		